const mongoose = require('mongoose');

// Define the Contact schema
const ContactSchema = new mongoose.Schema({
  userId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User', // Assuming you have a User model
    required: true
  },
  name: {
    type: String,
    required: true,
  },
  homePhone: {
    type: String,
    required: false,
  },
  workPhone: {
    type: String,
    required: false,
  },
  mobilePhone: {
    type: String,
    required: false,
  },
  relationType: {
    type: String,
    enum: ['Carer', 'Family', 'Neighbour', 'Friend'], // Only allow these values
    required: true,
  },
  email: {  // Add the email field
    type: String,
    required: false,
  },
  createdAt: {
    type: Date,
    default: Date.now,
  },
  updatedAt: {
    type: Date,
    default: Date.now,
  },
});

// Create a model from the schema
const Contact = mongoose.model('Contact', ContactSchema, 'contacts');

module.exports = Contact;
