const mongoose = require('mongoose');

// Define the schema for the DentistVisit model
const DentistVisitSchema = new mongoose.Schema({
  date: { type: Date, required: true },
  time: { type: String, required: true },
  description: { type: String, required: true },
  userId: { type: mongoose.Schema.Types.ObjectId, ref: 'User', required: true }
});

// Create the DentistVisit model using the schema
const DentistVisit = mongoose.model('DentistVisit', DentistVisitSchema, 'dentistvisits');

module.exports = DentistVisit;
