const mongoose = require('mongoose');

// Define the DoctorContact schema
const DoctorContactSchema = new mongoose.Schema({
  userId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User', // Referencing the User model
    required: true
  },
  contactType: {
    type: String,
    enum: ['Doctor', 'Consultant'], // Allow only 'Doctor' or 'Consultant' as values
    required: true
  },
  name: {
    type: String,
    required: true
  },
  address: {
    type: String,
    required: true
  },
  telephone: {
    type: String,
    required: true
  },
  email: {
    type: String,
    required: true
  },
  createdAt: {
    type: Date,
    default: Date.now
  },
  updatedAt: {
    type: Date,
    default: Date.now
  }
});

// Create a model from the schema
const DoctorContact = mongoose.model('DoctorContact', DoctorContactSchema, 'doctorContacts');

module.exports = DoctorContact;
