const mongoose = require('mongoose');

// Define the schema for the GPVisit model
const GPVisitSchema = new mongoose.Schema({
  date: { type: Date, required: true },
  time: { type: String, required: true },
  description: { type: String, required: true },
  userId: { type: mongoose.Schema.Types.ObjectId, ref: 'User', required: true }
});

// Create the GPVisit model using the schema
const GPVisit = mongoose.model('GPVisit', GPVisitSchema, 'gpvisits');

module.exports = GPVisit;
