const mongoose = require('mongoose');

// Define the schema for the HospitalVisit model
const HospitalVisitSchema = new mongoose.Schema({
  date: { type: Date, required: true },
  time: { type: String, required: true },
  description: { type: String, required: true },
  userId: { type: mongoose.Schema.Types.ObjectId, ref: 'User', required: true }
});

// Create the HospitalVisit model using the schema
const HospitalVisit = mongoose.model('HospitalVisit', HospitalVisitSchema, 'hospitalvisits');

module.exports = HospitalVisit;
