const mongoose = require("mongoose");

// Define the schema for the reminder
const reminderSchema = new mongoose.Schema({
  userId: { type: mongoose.Schema.Types.ObjectId, ref: 'User', required: true }, // Added userId
  initialDate: { type: Date, required: true },
  frequency: { type: Number, required: true }, // frequency in weeks
  nextCollectionDate: { type: Date, required: true },
});

// Create a model using the schema
const Reminder = mongoose.model("Reminder", reminderSchema, 'reminders');

module.exports = Reminder;
