const mongoose = require('mongoose');

// Define the schema for the Medication model
const MedicationSchema = new mongoose.Schema({
    medicationName: {
        type: String,
        required: true
    },
    dosage: {
        type: String, // Stores combined dosage amount and unit
        required: true,
      },
    frequency: {
        type: String,
        required: true
    },
    userId: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'User', // Referencing the User model
        required: true
    }
});

// Create the Medication model using the schema
const Medication = mongoose.model('Medication', MedicationSchema);

module.exports = Medication;
